    private void search_client_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_search_client_btnActionPerformed

        try {
            int id = Integer.parseInt(id_input.getText());
            Node_Reservation search_input = Global.getReservations().search(id, Global.getReservations().getRoot());
            output.setText("INFORMACIÓN DE LA RESERVA:\n\nTipo de habitación: " + search_input.getReservation().getRoom_type() + "\nFecha de llegada: " + search_input.getReservation().getArrival() + "\nFecha de salida: " + search_input.getReservation().getDeparture() + "\n\nREALIZADA POR EL CLIENTE:\n\n" + search_input.getReservation().getClient().showInfo());
            } catch (Exception e) {
                JOptionPane.showMessageDialog(null, "No existe ninguna reservación para este cliente.");
            }
    }//GEN-LAST:event_search_client_btnActionPerformed

