    private void search_client_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_search_client_btnActionPerformed
        try {
            int num = Integer.parseInt(room_num.getText());
            Node_Room room = Global.getRooms().search(num, Global.getRooms().getRoot());
            String result = room.getRoom().printHistory();
            output.setText(result);
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "¡No existe la habitación ingresada!");
        }
    }//GEN-LAST:event_search_client_btnActionPerformed

